                var currSelect = jQuery('#' + thisHandle.attr('id').split('handle_')[1]);
                currSelect.find('option').eq(ui.value).attr('selected', 'selected');

                $("#min-disp").val($("#valueA").val() + ' px');
                $("#max-disp").val($("#valueB").val() + ' px');
