/**
 * Fix element to top. Hidden when you scroll down, appears when you scroll 
 * up.
 * 
 * @param {number} settings.speed - Speed of the show/hide animation. Default
 * is 'fast'.
 * @param {number} settings.treshold - How many pixels should the user be able 
 * to scroll before triggering the hide animation.
 * @param {number} settings.buffer - Speed of the show/hide animation.
 */
jQuery.fn.fixToTop = function(settings){
    var element = jQuery(this);
    var elementHidden = false;
    var elementTopValue = element.css("top");
    var buffer = -20; // Moving the element extra pixels.
    var treshold = '150'; // 
    var speed = 'fast'; // Speed of the show/hide animation.

    $("#header-bar").mouseenter(function(){
        showElement();
    });

    function showElement() {
        if(elementHidden) {
            element.stop().animate(
            {
                top: (elementTopValue)
            }, 
            speed, 
            function() {
            });
        }
        elementHidden = false;
    }

    function hideElement() {
        if(!elementHidden) {
            element.stop().animate(
            {
                top: (0 + buffer + "px")                                     
            }, 
            speed,
            function() {
            });
        }
        elementHidden = true;
    }

    var yCoord = 0;
    var yCoordPrev = 0;
    var delta = 0;

    $(window).scroll(function () {
        yCoordPrev = yCoord; // Add previous coords
        yCoord = $(window).scrollTop(); // Get new coords
        delta = yCoordPrev - yCoord; // Calculate delta
        
        if(delta > 0) {
            showElement();
        }
        else {
            if(yCoord > treshold) {
                hideElement()
            }
        }
    });
    return this;
};
