Handlebars.registerHelper('format_size', function(fileSize) {
    // Bail immediately if we're below a KiB.
    if (fileSize < 1000)
        return fileSize + ' B';
    
    // Determine the divisor.
    var divisor, unit;
    
    if (fileSize >= 1073741824) {
        divisor = 1073741824.0;
        unit = 'GiB';
    }
    else if (fileSize > 1048576) {
        divisor = 1048576.0;
        unit = 'MiB';
    }
    else {
        divisor = 1024.0;
        unit = 'KiB';
    }
    
    return (parseFloat(fileSize) / divisor).toPrecision(3) + ' ' + unit;
});

Handlebars.registerHelper('percentage', function(progress, total) {
    return parseInt(parseFloat(progress) * 100.0 / parseFloat(total), 10);
});

Handlebars.registerHelper('format_money', Nextfinder.formatting.formatMoney);

Handlebars.registerHelper('verbal_join', function(items) {
    if (items.length == 0)
        return '';
    if (items.length == 1)
        return items[0];
    
    return items.slice(0, items.length - 1).join(', ') + ' and ' + items[items.length - 1];
});

