(function(window, $, undefined) {
    var scope = window.Nextfinder;
    
    /**
     * Submission file.
     * 
     * Contains a single file submission.
     * 
     * @class SubmissionFile
     * @private
     */
    var SubmissionFile = scope.Class.extend({
        /**
         * Submission file ID.
         * 
         * @member _id
         * @type {int}
         */
        
        
        /**
         * File name.
         * 
         * @member name
         * @type {string}
         */
        
        
        /**
         * File size.
         * 
         * @member size
         * @type {int}
         */
        
        
        /**
         * Data.
         * 
         * Contextually bound data.
         * 
         * @member data
         * @type {object}
         */
        
        
        /**
         * Error.
         * 
         * @member error
         * @type {string}
         */
        
        
        /**
         * State.
         * 
         * Will be set internally to reflect the current state of the file 
         * submission.
         * 
         * @member state
         * @type {string}
         */
        
        
        STATE_UPLOADING: 'uploading', 
        STATE_ERROR: 'error', 
        STATE_DONE: 'done', 
        
        
        /**
         * Initialize a submission file.
         * 
         * @param {string} url Optional submission file URL.
         * @param {string} name File name.
         * @param {int} size File size.
         * @param {object} data Contextual data.
         * @param {string} error Error.
         */
        init: function(url, name, size, data, error) {
            this.url = url;
            this.name = name;
            this.size = size;
            this.data = data;
            this.error = error;
            this.progress = (data ? {
                total: size, 
                loaded: 0, 
                percentage: 0
            } : null);
            
            if (data)
                this.state = this.STATE_UPLOADING;
            else if (error)
                this.state = this.STATE_ERROR;
            else
                this.state = this.STATE_DONE;
        },
        
        
        /**
         * Set an error on the submission file.
         * 
         * Clears the contextual data association and updates the state.
         * 
         * @param {string} error Error.
         */
        setError: function(error) {
            this.state = this.STATE_ERROR;
            this.error = error;
            this.data = null;
        }, 
        
        
        /**
         * Set the submission file as done.
         * 
         * Clears the contextual data association and updates the state.
         * 
         * @param {string} url Submission file URL after finished upload.
         */
        setDone: function(url) {
            this.url = url;
            this.state = this.STATE_DONE;
            this.data = null;
        }
    });
    
    
    /**
     * Upload view for icon set submission.
     * 
     * @class Nextfinder.iconsets.newUpload
     */
    scope.iconsets.newUpload = scope.Class.extend({
        /**
         * Files.
         * 
         * @member _files
         * @private
         * @type {SubmissionFile[]}
         */
        
        
        /**
         * Navigation bars.
         * 
         * @member _navigationBars
         * @private
         * @type {$}
         */
        
        
        /**
         * File container.
         * 
         * @member _fileContainer
         * @private
         * @type {$}
         */
        
        
        /**
         * Upload form.
         * 
         * @member _form
         * @private
         * @type {$}
         */
        
        
        /**
         * Bind events.
         * 
         * @private
         */
        _initBindings: function() {
            var _this = this;
        
            ///$('#action-delete').tableAction(function(dataIds) {
            //    _this._confirmDelete(dataIds);
            //});

            this._fileContainer.on('click', '[data-action="remove"]', function(event) {
                event.preventDefault();
                
                // Retrieve the container element and the file being removed.
                var containerElement = $(event.currentTarget).closest('[data-type="file"]');
                if (!containerElement)
                    return false;
                var file = containerElement.data('file');
                
                // Cancel the upload if it is ongoing.
                if ((file.data) && (file.data.jqXHR)) {
                    file.data.jqXHR.abort();
                    file.data.submissionFile = null;
                    file.data = null;
                }
                
                if (file.url) {
                    // Send a delete request.
                    $.ajax(file.url, {
                        cache: false, 
                        dataType: 'json', 
                        type: 'DELETE'
                    }).done(function() {
                        // Remove it from the list of files.
                        _this._files.splice(_this._files.indexOf(file), 1);
                        _this._renderFiles();
                    });
                }
                else {
                    // Remove it from the list of files immediately.
                    _this._files.splice(_this._files.indexOf(file), 1);
                    _this._renderFiles();
                }
                
                return false;
            });
        }, 
        
        
        /**
         * Initialize the view.
         * 
         * Attempts to read already uploaded files from the `UPLOADED_FILES` 
         * global variable.
         */
        init: function() {
            var _this = this;
            
            // Create the local list of files.
            this._files = [];
            
            // Read in each file if any are available.
            if (window.UPLOADED_FILES)
                $.each(window.UPLOADED_FILES, function(index, file) {
                    _this._files.unshift(SubmissionFile.create(file.url, 
                                                               file.name, 
                                                               file.size));
                });
            
            // Resolve each of the containers.
            this._navigationBars = $('#files-navigation-top, #files-navigation-bottom');
            this._fileContainer = $('#files');
            this._form = $('#upload');
            
            // Initialize bindings.
            this._initBindings();
            
            // Start out by performing the first render.
            this._renderFiles();
            
            // Initialize the upload area to use the file upload widget.
            $('#upload').fileupload({
                /**
                 * Automatically begin uploads.
                 */
                autoUpload: true, 
                
                
                /**
                 * Add callback.
                 * 
                 * @param {Event} e Event.
                 * @param {object} data Data containin a single file.
                 */
                add: function(e, data) {
                    // We expect there to only be one file in each call to this 
                    // method.
                    if (data.files.length != 1) {
                        console.error('One file expected in the supplied data to .add', data);
                        return;
                    }
                    file = data.files[0];
                    
                    // Validate the file.
                    var error = null;
                    
                    if (!/(\.|\/)(gif||png|svg|zip|txt|md)$/i.test(file.name))
                        error = 'Unsupported file type';
                    else if (file.size > 52428800)
                        error = 'File size over limit (50 MiB)';

                    var submissionFile = SubmissionFile.create(null, 
                                                               file.name, 
                                                               file.size, 
                                                               (error !== null ? null : data), 
                                                               error);
                    
                    if (error === null) {
                        // Submit the data.
                        data.submissionFile = submissionFile;
                        data.submit();
                    }
                    
                    
                    // Stop! Render time!
                    _this._files.unshift(submissionFile);
                    _this._renderFiles();
                    _this._updateSubmit();               
                },
                
                
                /**
                 * Failure callback.
                 * 
                 * Invoked when an upload fails.
                 */
                fail: function(e, data) {
                    // Update the state of the submission.
                    var submissionFile = data.submissionFile;
                    data.submissionFile = null;
                    submissionFile.setError('Upload failed');
                    _this._renderFiles();

                    $.pnotify({
                        pnotify_title: 'File upload failes',
                        pnotify_text: 'The files you tried to upload failed.',
                        type: 'error'
                    });
                    _this._updateSubmit();               
                }, 
                
                
                /**
                 * Done callback.
                 * 
                 * Invoked when an upload finishes.
                 */
                done: function(e, data) {
                    // Update the state of the submission.
                    var width = 100;

                    try{
                        submissionFile.container.find('.progress .bar').width(width + '%');                       
                    }
                    catch(err) {
                        console.log(err);
                    }

                    var submissionFile = data.submissionFile;
                    data.submissionFile = null;
                    submissionFile.setDone(data.result.url);
                    _this._renderFiles();
                    _this._updateSubmit();               
                }, 
                
                
                /**
                 * Progress callback.
                 */
                progress: function(e, data) {
                    var submissionFile = data.submissionFile;
                    if (!submissionFile.container)
                        return;
                    
                    submissionFile.progress = {
                        loaded: parseInt(data.loaded, 10), 
                        total: parseInt(data.total, 10), 
                        percentage: parseFloat(data.loaded) * 100.0 / parseFloat(data.total)
                    };
                    
                    var width = parseInt(submissionFile.progress.percentage, 10);
                    submissionFile.container.find('.progress .bar').width(width + '%');
                    _this._updateSubmit();               
                }
            });
        },
        
        /**
        * Disable submit button when uploading.
        **/
        _updateSubmit: function() {
            var isUploading = false;

            $.each(this._files, function() {
                if (this.state == "uploading")
                    isUploading = true;
            });

            if (isUploading) {
                $('.import-files').html("Uploading files ...").attr("disabled","disabled");
            }
            else {
                $('.import-files').html("Import files").removeAttr("disabled");
            }
        },


        
        /**
         * Render the file list.
         * 
         * Fills the files container with the rendering result of the 
         * `new_upload_files.html` template after which the individual file 
         * representations are assigned as the contextual data of each 
         * element represented by the `data-state` attribute.
         * 
         * @private
         */
        _renderFiles: function() {
            // Bail quickly if no files are available.
            if (!this._files.length) {
                this._navigationBars.hide();
                this._fileContainer.html('').hide();
                return;
            }
            
            // Make sure the navigation bars are visible.
            this._navigationBars.show();
            
            // Render out the files.
            var childrenHtml = Handlebars.templates['iconsets/templates/new_upload_files.html']({
                files: this._files
            });

            var children = this._fileContainer.html(childrenHtml).show().children();

            $(".js-sticky").sticky({ topSpacing: 0, className: 'is-js-sticky', wrapperClassName: 'js-sticky-wrapper' });
            
            // Assign context to uploading files.
            var _this = this;
            children.find('[data-type="file"]').each(function(index, element) {
                $(element).data('file', _this._files[index]);
                _this._files[index].container = $(element);
            });
        }
    });
})(window, window.jQuery);

