(function(window, document, $, undefined) {
    var scope = window.Nextfinder;
    
    var ICON_DROP_TARGET_SELECTOR = '.management-container .version-container .drop-cover', 
        SPACER_SELECTOR = '.management-container .spacer';
    
    /**
     * Manage icons.
     * 
     * @class Nextfinder.iconsets.manage_icons
     */
    scope.iconsets.edit_icons = scope.Class.extend({
        /**
         * Package ID.
         * 
         * @type {string}
         * @private
         */
        _packageId: null, 
        
        
        /**
         * Initialize a container.
         * 
         * Initializes the dynamic elements of an icon container.
         * 
         * @private
         */
        initializeContainer: function(container) {
            container.find('.tags input[type="text"]').tagsInput();
            container.find('.tags .tagsinput').css({
                'width': '', 
                'height': '', 
            });
        }, 
        
        
        /**
         * Delete an icon.
         * 
         * @param {$} container Icon container.
         * @private
         */
        deleteIcon: function(container) {
            container.fadeOut('fast', function() {
                container.next('.spacer').remove();
                container.remove();
            });
        },
        
        
        /**
         * Update an icon.
         * 
         * Updates the state of all the elements in an icon container and 
         * deletes it if necessary.
         * 
         * @param {$} container Icon container.
         * @private
         */
        updateIcon: function(container) {
            // Delete the icon if no versions are available.
            var versions = container.find('.version');
            if (versions.length == 0)
                this.deleteIcon(container);
            
            // Disable the delete buttons if there's only one version 
            // left in the icon.
            var deleteButtons = versions.find('a.delete');
            
            if (versions.length == 1)
                deleteButtons.hide();
            else
                deleteButtons.show();
        },
        
        
        /**
         * Create an icon.
         * 
         * Creates and initializes a new icon.
         * 
         * @param {$} after Element after which to insert the container.
         * @param {string} tags Initial tags for the icon.
         * @param {bool} isActive Whether the icon is to be active.
         * @returns {$} the created icon container.
         * @private
         */
        createIcon: function(after, 
                             tags, 
                             isActive) {
            // Create a spacer.
            var spacer = $('<div class="spacer">').insertAfter(after);
            
            // Create the empty icon container.
            var container = $(Handlebars.templates['iconsets/templates/edit_icons_icon.html']({
                package_id: this._packageId, 
                tags: tags, 
                is_active: isActive
            })).insertAfter(after);
            
            // Initialize the container.
            this.initializeContainer(container);
            
            return container;
        },
        
        
        /**
         * Submit changes.
         * 
         * Serializes the icons and submits them to the backend.
         * 
         * @private
         */
        submit: function() {
            var self = this;
            
            // * List all the icons.
            var icons = [];
            
            $('[data-icon-id]').each(function() {
                if (icons === undefined)
                    return;
                
                var iconContainer = $(this), 
                    iconId = iconContainer.attr('data-icon-id'), 
                    tags = iconContainer.find('input[type="text"]').val(), 
                    versions = [];
                
                // List all the versions.
                iconContainer.find('[data-iconversion-id]').each(function() {
                    versions.push($(this).attr('data-iconversion-id'));
                });
                
                // Only append the icon if it has versions.
                if (versions.length == 0)
                    return;
                
                // Check that the icon actually has tags.
                if ($.trim(tags) == '') {
                    icons = undefined;
                    alert('Error: one or more icons have no tags');
                    return;
                }
                
                icons.push({
                    iconId: (iconId == 'new' ? null : iconId), 
                    isActive: (iconContainer.attr('data-active') == 'true'), 
                    tags: tags, 
                    versions: versions
                });
            });
            
            if (icons === undefined)
                return;
            
            // * Construct and send a POST.
            var postData = {
                icon_count: icons.length
            }
            
            var iconIndex = icons.length;
            while (iconIndex--) {
                var icon = icons[iconIndex];
                
                postData['icon_' + iconIndex + '_icon_id'] = icon.iconId;
                postData['icon_' + iconIndex + '_tags'] = icon.tags;
                postData['icon_' + iconIndex + '_is_active'] = (icon.isActive ? 'true' : 'false');
                postData['icon_' + iconIndex + '_versions'] = icon.versions.join(',');
            }
            
            var overlay = $('body').overlayLoader('Saving changes...');
            
            // Send the POST request.
            $.ajax({
                type: 'POST', 
                url: '/iconsets/' + this._packageId + '/icons/edit', 
                dataType: 'json', 
                data: postData, 
                success: function(data) {
                    location.href = '/iconsets/' + self._packageId + '/icons';
                }, 
                error: function() {
                    overlay.remove();
                    alert('Failed to save changes. Please try again or reload the page if the problem persists.');
                }
            });
        }, 
        
        
        /**
         * Initialize the view.
         */
        init: function() {
            var self = this;
            
            ////////////////////////////////////////////////////////////////////
            // Drag and drop functionality.
            
            // Bind dragging events to dragging icon versions.
            this.isDraggingVersion = false;
            this.draggingVersion = null;
            
            $(document).on('dragstart', '[data-iconversion-id]', function(event) {
                // Register that we're now dragging an icon version.
                self.isDraggingVersion = true;
                self.draggingVersion = $(this);
                
                var closestVersionContainer = $(this).closest('.version-container');
                
                $('.management-container .version-container').each(function() {
                    if (this != closestVersionContainer[0])
                        $(this).append('<div class="drop-cover"></div>');
                });
            }).on('dragend', '[data-iconversion-id]', function() {
                // Register that we're no longer dragging an icon version.
                self.isDraggingVersion = false;
                
                // Clear spacers and placeholders.
                var container = $('.management-container');
                container.find('.version.ghost, .version-container > .drop-cover').remove();
                container.find('.spacer-drop-target').removeClass('spacer-drop-target').html('');
            });
            
            // Bind drop target events to spacers.
            var spacerTimeout = null;
            
            function preventDefaultIfIsDraggingVersion(event) {
                if (!self.isDraggingVersion)
                    return;
                
                event.preventDefault();
                return false;
            }
            
            $(document)
                .on('dragenter', SPACER_SELECTOR, function(event) {
                    if (preventDefaultIfIsDraggingVersion(event) !== false)
                        return;
                    
                    var spacer = $(this);
                    spacerTimeout = setTimeout(function() {
                        spacerTimeout = null;
                        spacer.addClass('spacer-drop-target').html('Create new icon');
                    }, 100);
                    
                    return false;
                })
                .on('dragover', SPACER_SELECTOR, preventDefaultIfIsDraggingVersion)
                .on('dragleave', SPACER_SELECTOR, function() {
                    $(this).removeClass('spacer-drop-target').html('');
                    if (spacerTimeout)
                        clearTimeout(spacerTimeout);
                })
                .on('drop', SPACER_SELECTOR, function(event) {
                    if (!self.isDraggingVersion)
                        return;
                    
                    // Determine the icon to which the dragging version 
                    // belongs.
                    var fromIcon = self.draggingVersion.closest('[data-icon-id]');
                    
                    // Create a new icon.
                    var toIcon = self.createIcon($(this), 
                                                 fromIcon.find('input[type="text"]').val(), 
                                                 fromIcon.attr('data-active') == 'true');
                    
                    // Remove the dragging icon version from the old icon.
                    self.draggingVersion.remove().appendTo(toIcon.find('.version-container'));
                    
                    // Update the icon's state.
                    self.updateIcon(fromIcon);
                    self.updateIcon(toIcon);
                });
            
            // Bind drop target events to icons.
            $(document)
                .on('dragenter', ICON_DROP_TARGET_SELECTOR, function(event) {
                    if (preventDefaultIfIsDraggingVersion(event) !== false)
                        return;
                    
                    $(this).parent().append('<div class="version ghost">&nbsp;</div>');
                    return false;
                })
                .on('dragover', ICON_DROP_TARGET_SELECTOR, preventDefaultIfIsDraggingVersion)
                .on('dragleave', ICON_DROP_TARGET_SELECTOR, function() {
                    $(this).parent().find('.version.ghost').remove();
                })
                .on('drop', ICON_DROP_TARGET_SELECTOR, function(event) {
                    if (!self.isDraggingVersion)
                        return;
                    
                    // Determine the icon to which the dragging version 
                    // belongs, and the icon to which it will belong after this 
                    // operation succeeds.
                    var fromIcon = self.draggingVersion.closest('[data-icon-id]'), 
                        toIcon = $(this).closest('[data-icon-id]');
                    
                    // Remove the dragging icon version from the old icon.
                    self.draggingVersion.remove().appendTo(toIcon.find('.version-container'));
                    
                    // Update the icon's state.
                    self.updateIcon(fromIcon);
                    self.updateIcon(toIcon);
                });
            
            ////////////////////////////////////////////////////////////////////
            // Icon editing functionality.
            
            $(document)
                .on('click', '[data-action="delete-icon"]', function(event) {
                    if (!confirm("Are you sure you want to delete this icon?\n\nThere's no way back, once you've deleted it. Seriously."))
                        return false;
                    
                    self.deleteIcon($(this).closest('[data-icon-id]'));
                    
                    return false;
                })
                .on('click', '[data-action="toggle-icon-active"]', function(event) {
                    var container = $(this).closest('[data-icon-id]');
                    
                    if (container.attr('data-active') == 'true') {
                        container.attr('data-active', 'false');
                        $(this).removeClass('is-active');
                    }
                    else {
                        container.attr('data-active', 'true');
                        $(this).addClass('is-active');
                    }
                    
                    return false;
                })
                .on('click', '[data-action="delete-version"]', function(event) {
                    if (!confirm("Are you sure you want to delete this icon version?\n\nSomeone might need it, you know."))
                        return false;
                    
                    // Remove the version.
                    var iconContainer = $(this).closest('[data-icon-id]'), 
                        version = $(this).closest('.version');
                    version.remove();
                    
                    // Update the icon.
                    self.updateIcon(iconContainer);
                    
                    return false;
                })
                .on('click', '[data-action="submit"]', function(event) {
                    self.submit();
                    return false;
                });
            
            // Determine the package ID.
            this._packageId = $('.management-container [data-package-id]').attr('data-package-id');
            
            // Initialize the initial icon containers.
            this.initializeContainer($('.management-container'));
        }
    });
})(window, window.document, window.jQuery);

