(function(window, Nextfinder, $, undefined) {
    /**
     * Asset purchase flow.
     * 
     * State machine offering flow based control of the purchase of an asset.
     * 
     * Currently, the states themselves are not fully utilized nor validated, 
     * which should be changed in the future when the state machine 
     * functionality is extracted into a separate base class.
     * 
     * @class AssetPurchaseFlow
     */
    var AssetPurchaseFlow = Nextfinder.Class.extend({
        //////////////////////////////////////////////////////////////////////
        // State machine related functionality.
        
        /**
         * Initialize an asset purchase flow.
         * 
         * Sets up and runs the asset purchase flow for the given asset.
         * 
         * @option {object} asset Asset descriptor containing the asset 
         * identifier as the `identifier` member and the primary key of the 
         * asset as the `primary_key` member.
         * @option {function()} success Callback for successful purchase.
         * @param {object} options Options.
         */
        init: function(options) {
            var self = this;
            
            // Combine the settings.
            this.settings = $.extend({}, {
                
            }, options);
            
            // Generate a purchase request token to use for this flow.
            this.requestToken = $.generateRandomAlphanumeric(32);
            
            // Reset the state.
            this.state = null;
            this.isSuccessful = false;
            
            this.states = {
                gettingOffers: {}, 
                awaitingBillingDetails: {}, 
                handlingInsufficientFunds: {}, 
                awaitingCashDeposit: {}, 
                awaitingPurchaseConfirmation: {}, 
                verifyingBalance: {}, 
                failed: {}, 
                done: {},
                purchasing: {}, 
                displayingCompletion: {}
            };
            
            // Set up the modal environment.
            this.overlay = $('body').overlay();
            
            // Transition to the first state.
            this.getOffers();
        }, 
        
        
        /**
         * Update the user's balance.
         * 
         * @param {number} balance The user's current balance.
         */
        updateUserBalance: function(balance) {
            this.balance = balance;
            $('[data-content="user-balance"]')
                .html('$ ' + Nextfinder.formatting.formatMoney(balance));
        }, 
        
        
        /**
         * Transition to a state.
         * 
         * @param {string} state State identifier.
         */
        transitionTo: function(state) {
            var previousState = this.state;
            this.state = state;
            
            if ((previousState != null) && 
                (this.states[previousState].transitionTo))
                this.states[previousState].transitionTo(state);
            if (this.states[state].transitionFrom)
                this.states[state].transitionFrom(previousState);
        }, 
        
        
        //////////////////////////////////////////////////////////////////////
        // Events.
        
        /**
         * Get the offers for the asset.
         * 
         * Asynchronously retrieves the offers available for the requested 
         * asset and transitions to the `gettingOffers` state awaiting the 
         * response.
         */
        getOffers: function() {
            var self = this;
            
            // Show a loader in the overlay.
            this.overlay.overlayLoader('Hang tight...');
            
            // Resolve the offers.
            Nextfinder.transport.get({
                url: '/commerce/ajax/offers', 
                data: {
                    asset_identifier: this.settings.asset.identifier, 
                    asset_primary_key: this.settings.asset.primary_key
                }, 
                success: function(data) {
                    // Store the available information.
                    self.asset = data.asset;
                    self.offer = data.offers[0];
                    self.user = data.user;
                    
                    self.updateUserBalance(data.balance);
                    
                    // Clear the overlay.
                    self.overlay.html('');
                    
                    // Deal with missing account details.
                    if ((!data.user.address_street) || 
                        (!data.user.address_city) || 
                        (!data.user.address_postal_code) || 
                        (!data.user.address_country)) {
                        self.getBillingDetails(data.user);
                        return;
                    }
                    
                    // Show the modal fitting the situation.
                    if (self.balance < self.offer.price)
                        self.handleInsufficientFunds();
                    else
                        self.confirmPurchase();
                }, 
                error: function(code, data) {
                    self.overlay.html('');
                    
                    // Basically, this should not happen.
                    self.fail('Something is awry', 'Something went wrong here. Please refresh the site and try again.<br /><br />If the problem persists, please do not hesitate to contact <a href="http://support.iconfinder.com/">our support</a>.');
                }
            });
            
            this.transitionTo('gettingOffers');
        }, 
        
        
        /**
         * Fail the purchasing flow.
         * 
         * Displays a modal explaining the reason for failure while 
         * transitioning to the `failed` state.
         * 
         * @param {string} title Failure explanation title.
         * @param {string} message Failure explanation message.
         */
        fail: function(title, message) {
            var self = this;
            
            Nextfinder.modals.ErrorModal.create({
                title: title, 
                message: message, 
                backdrop: false, 
                close: function() {
                    self.finish();
                }
            });
            
            this.transitionTo('failed');
        }, 
        
        
        /**
         * Finish the flow.
         * 
         * Dismisses the backdrop overlay and transitions to the done state.
         */
        finish: function() {
            var overlay = this.overlay;
            
            overlay.fadeOut(200, function() {
                overlay.remove();
            });
            
            if (this.isSuccessful)
                this.settings.success();
            
            this.transitionTo('done');
        }, 
        
        
        /**
         * Get billing details.
         * 
         * Displays a modal requesting the billing details necessary for 
         * performing a purchase.
         * 
         * Transitions to the `awaitingBillingDetails` state.
         * 
         * @param {object} user User model.
         */
        getBillingDetails: function(user) {
            var self = this;
            
            Nextfinder.modals.BillingInformationModal.create({
                user: user, 
                backdrop: false, 
                success: function() {
                    self.getOffers();
                }, 
                cancel: function() {
                    self.finish();
                }
            });
            
            this.transitionTo('awaitingBillingDetails');
        }, 
        
        
        /**
         * Confirm the purchase.
         * 
         * Displays a modal requesting the confirmation for purchasing the 
         * selected asset at the retrieved offer price and license.
         * 
         * Transitions to the `awaitingPurchaseConfirmation` state.
         */
        confirmPurchase: function() {
            var self = this;
            
            var assetContext = {};
            
            if (this.asset.type == 'icon')
                assetContext = {
                    price: this.offer.price, 
                    available_as: this.asset.available_as, 
                    display_version: this.asset.display_version, 
                    "package": this.asset["package"]
                };
            else if (this.asset.type == 'package')
                assetContext = {
                    price: this.offer.price, 
                    name: this.asset.name
                };
            
            var modal = Nextfinder.modals.PurchaseConfirmationModal.create({
                context: {
                    asset: assetContext, 
                    license: this.offer.license, 
                    asset_is_icon: (this.asset.type == 'icon'), 
                    asset_is_package: (this.asset.type == 'package')
                }, 
                cancel: function() {
                    self.finish();
                }, 
                action: function() {
                    var productId, 
                        productType, 
                        productName;

                    var vat = 0;
                    var designer = 'TBD';

                    if (self.asset.type == 'icon') {
                        productId = 'icon-' + self.asset.icon_id;
                        productType = 'icon';
                        productName = self.asset.tags.join(', ') + ' icon';
                    }
                    else if (self.asset.type == 'package') {
                        productId = 'iconset-' + self.asset.package_id;
                        productType = 'iconset';
                        productName = self.asset.name;
                    }
                    else {
                        console.log('Cannot handle asset type ' + self.asset.type); 
                    }
                    
                    Nextfinder.tracking.trackTransaction(
                        self.requestToken,
                        designer,
                        self.offer.price,
                        vat,
                        productId,    
                        productName,              
                        productType, 
                        self.user.address_city,
                        self.user.address_state,
                        self.user.address_country
                    );

                    modal.close();
                    self.purchase(self.offer);
                }, 
                backdrop: false
            });
            
            this.transitionTo('awaitingPurchaseConfirmation');
        }, 
        
        
        /**
         * Request cash deposit.
         * 
         * Displays a modal requesting the user to make a deposit.
         * 
         * Transitions to the `awaitingCashDeposit` state.
         */
        requestCashDeposit: function() {
            var self = this;
            
            this.overlay.overlayLoader('Hang tight...');
            
            Nextfinder.transport.get({
                url: '/commerce/ajax/credit-cards', 
                success: function(data) {
                    self.overlay.html('');
                    
                    var modal = Nextfinder.modals.DepositCashModal.create({
                        cancel: function() {
                            self.finish();
                        }, 
                        success: function() {
                            modal.close();
                            self.verifyBalance();
                        }, 
                        backdrop: false, 
                        creditCards: data.credit_cards
                    });
                }, 
                error: function(code) {
                    self.overlay.html('');
                    self.fail('Something went wrong', 'Sorry, but it seems that we cant get a hold of the servers. Please reload the page and try again.');
                }
            });
            
            this.transitionTo('awaitingCashDeposit');
        }, 
        
        
        /**
         * Handle insufficient funds.
         * 
         * Transitions to the `handlingInsufficientFunds`.
         */
        handleInsufficientFunds: function() {
            var self = this;
            
            var modal = Nextfinder.modals.InsufficientFundsModal.create({
                balance: self.balance, 
                deposit: function() {
                    self.requestCashDeposit();
                }, 
                cancel: function() {
                    self.finish();
                }, 
                backdrop: false
            });
            
            this.transitionTo('handlingInsufficientFunds');
        }, 
        
        
        /**
         * Purchase the asset.
         * 
         * Transitions to the `purchasing` state.
         * 
         * @param {object} offer Offer to purchase.
         */
        purchase: function(offer) {
            var self = this;
            
            this.overlay.overlayLoader('Hang tight...');
            
            Nextfinder.transport.post({
                url: '/commerce/ajax/purchase', 
                success: function(data) {
                    self.updateUserBalance(data.balance);
                    self.isSuccessful = true;
                    self.overlay.html('');
                    if (data.display_post_purchase)
                        self.displayCompletion();
                    else
                        self.finish();
                }, 
                error: function(code, data) {
                    self.overlay.html('');
                    if (code == 'insufficient_funds')
                        self.handleInsufficientFunds();
                    else
                        self.fail('Ooops', "We don't know why, but for some reason, the purchase fell through. Please reload the page and try again");
                }, 
                data: {
                    asset_identifier: self.settings.asset.identifier, 
                    asset_primary_key: self.settings.asset.primary_key, 
                    license_id: offer.license.license_id, 
                    request_token: self.requestToken
                }
            });
            
            this.transitionTo('purchasing');
        }, 
        
        
        /**
         * Verify the balance.
         * 
         * Requests the user's current balance and correlates that with the 
         * offer price.
         * 
         * Transitions to the `verifyingBalance` state.
         */
        verifyBalance: function() {
            var self = this;
            self.overlay.overlayLoader('Hang tight...');
            
            Nextfinder.transport.get({
                url: '/account/ajax/state', 
                success: function(data) {
                    self.overlay.html('');
                    
                    self.updateUserBalance(data.balance);
                    
                    if (self.balance < self.offer.price)
                        self.handleInsufficientFunds();
                    else
                        self.confirmPurchase();
                }, 
                error: function() {
                    self.overlay.html('');
                    self.fail('Ooops', "We don't know why, but for some reason, we can't get your current balance. Please reload the page and try again.");
                }
            });
            
            this.transitionTo('verifyingBalance');
        }, 
        
        
        /**
         * Display purchase completion modal dialog.
         * 
         * Transitions to the `displayingCompletion`.
         */
        displayCompletion: function() {
            var self = this;
            
            Nextfinder.modals.PurchaseCompletedModal.create({
                closeWithState: function(dontShowAgain) {
                    if (dontShowAgain)
                        Nextfinder.transport.post({
                            url: '/commerce/ajax/never-show-post-purchase', 
                            success: function() {}, 
                            error: function() {}
                        });
                    self.finish();
                }, 
                backdrop: false
            });
            
            this.transitionTo('displayingCompletion');
        }
    });
    
    
    /**
     * Purchase an asset.
     * 
     * The following options are supported:
     * 
     * @option {object} asset Asset descriptor containing an identifier and 
     * a primary key.
     * @option {function()} success Callback for successful asset purchase.
     * 
     * @param {object} options Purchase options.
     */
    Nextfinder.commerce.purchase = function(options) {
        if (!window.USER) {
            Nextfinder.modals.AuthenticationNeededModal.create();
            return;
        }
        
        var flow = AssetPurchaseFlow.create(options);
    };
})(window, window.Nextfinder, window.jQuery);

