// Focus search field
function sfocus() {
    $("#inputField").focus();
    return false;
}

$(function() {
    $('input, textarea').placeholder();
});

// Handle keyboard navigation
$(document).keydown(handleKey);

function handleKey(e) {
    // Keyboard shortcuts for browsing pages of lists
    if($(".pagination").is(":visible")) {
        var left_arrow = 37;
        var right_arrow = 39;

        if (e.target.localName == 'body' || e.target.localName == 'html') {
            if (!e.ctrlKey && !e.altKey && !e.shiftKey && !e.metaKey) {
                var code = e.which;
                if (code == left_arrow)
                    prevPage();
                else if (code == right_arrow)
                    nextPage();
            }
        }
    }

    /*
    // Submit form if Enter is pressed.
    if($(".modal").is(":visible")) {
        console.log("Modal is visible");
        if (e.which == 13) 
            $(".modal:visible").find('form').submit();
    }
    */
}

function prevPage() {
    var href = $('.pageslist .prev').attr('href');
    if (href && href != document.location)
        document.location = href;
}

function nextPage() {
    var href = $('.pageslist .next').attr('href');
    if (href && href != document.location)
        document.location = href;
}


// Load results
function loadJsResults(){

    // TODO: Move this to icons.js
    $("#dropmenu").click(function() {
        showmenu();
    });

    $("#inputField").click(
    function() {
        sfocus();
        return false;
    });

    // Filters and settings
    $(".scrolling button").click(
    function() {
        $(".scrolling button").removeClass("active");
        $(this).addClass("active");
        updatecontscrolling();
        return false;
    });

    update = 0;
    loadinfo();

    /* Track external links */
    $("a.external").click(function() {
        Nextfinder.tracking.trackEvent('Links','External',$(this).attr('href'));
        return true;
    });

    return false;
}


