    var timeout    = 0;
    var closetimer = 0;
    var ddmenuitem = 0;

    function loadinfo() {
        document.onclick = hideallinfo;
        
        // Unbind/rebind click events as the new elements
        // are being dynamically added by Ajax
        $(".infolink").unbind('click');
        $(".infolink").click(
        function()
        {
            id = $(this).attr('id').replace(/infolink-/,"");
            obj = $("#icon-" + id);
            $("#infomenu-" + id).html('&nbsp;');
            showinfo(id, $(this).position().left);
            return false;
        });

        return false;
    }

    function showinfo(id, position) {
        canceltimerinfo();
        hideinfo($(document).find('.infomenu'));
        infomenu = $('#infomenu-'+id);
        $('#infolink-'+id).addClass("selected");
        infomenu.css("display","block");
        infomenu.css("left", (position - 152) + "px");
        infomenu.addClass("loading");
        hiddensize = $('#size-'+id).val();

        $.get("/ajax/infomenu/",
        {
            id: id,
            size: hiddensize
        },
        function(data)
        {
            infomenu.html(data);
            infomenu.removeClass("loading");
            Nextfinder.tracking.trackEvent('Search results', 'Info menu', 'Show info');
        });

        obj = null;
        //infomenu = null;
        id = null;
        hiddensize = null;
        return false;
    }

    function timerinfo(obj) {
        closetimer = window.setTimeout(hideallinfo, timeout);
        return false;
    }

    function hideallinfo() {
        hideinfo($(document).find('.infomenu'));
        hidemenu();
    }

    function hideinfo(obj) {
        obj.siblings('.infolink').removeClass("selected");
        obj.fadeOut('fast');
    }

    function canceltimerinfo() {
        if(closetimer) {
            window.clearTimeout(closetimer);
            closetimer = null;
        }
        return false;
    }

    var menushown = 0;

    function showmenu() {
        $("#menu-content").fadeIn();
        setTimeout(function(){menushown=1;},800);
    }

    function hidemenu() {
        if(menushown == 1) {
            $("#menu-content").fadeOut();
            menushown = 0;
        }
    }


